#include<bits/stdc++.h>
using namespace std;

int t,n;
int a[100];
string s;
char to[60];
bool dali;

int main(){

    ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);

    cin>>t;
    for(int i=1;i<=t;i++){
        cin>>n;
        for(int f=0;f<n;f++){
            cin>>a[f];
        }
        cin>>s;
        for(int f=0;f<60;f++)to[f]=-1;
        dali=true;

        for(int f=0;f<s.size();f++){
            if(to[a[f]]==-1)to[a[f]]=s[f];
            else if(to[a[f]]!=s[f]){
                dali=false; cout<<"NO\n"; break;
            }
        }

        if(dali)cout<<"YES\n";
    }

    return 0;
}
