#include <bits/stdc++.h>
#define DIM 57
using namespace std;

long long n,a[DIM],used[1007];
int main()
{
    cin>>n;
    for(int i = 1; i<= n; ++i)
        cin>>a[i];

    vector<long long> ans;
    for(int i = n;i>=1;--i)
        if(used[a[i]]==0)
    {
        used[a[i]] = 1;
        ans.push_back(a[i]);
    }

    reverse(ans.begin(),ans.end());
    cout<<ans.size()<<endl;
    for(auto i:ans)
        cout<<i<<' ';
    return 0;
}
