#include <bits/stdc++.h>

using namespace std;

int main() {
#ifdef _DEBUG
	freopen("input.txt", "r", stdin);
//	freopen("output.txt", "w", stdout);
#endif

	int n, x, y;
	cin >> n >> x >> y;
	int cnt = 0;
	for (int i = 0; i < n; ++i) {
		int cur;
		cin >> cur;
		if (cur <= x) {
			++cnt;
		}
	}

	if (x > y) {
		cout << n << endl;
	} else {
		cout << (cnt + 1) / 2 << endl;
	}

	return 0;
}
