#include <bits/stdc++.h>
#define DIM 200007
using namespace std;

long long n,m,a[DIM],p[DIM];


int main()
{
    cin>>n>>m;

    for(int i = 1; i <= n; ++i)
    {
        cin>>a[i];
        p[i] = p[i-1] + a[i];
    }

    for(int i = 1; i <= m; ++i)
    {
        long long b;
        cin>>b;
        int pos = lower_bound(p+1,p+1+n,b) - p;
        cout<<pos<<' '<<b-p[pos-1]<<endl;
    }
    return 0;

}
