#include <bits/stdc++.h>
#define DIM 1007
using namespace std;

long long n,w,a[DIM],mx,curr,mn;

int main()
{
    cin>>n>>w;
    for(int i = 1; i<=n;++i)
    {
        cin>>a[i];
        curr+=a[i];
        mx = max(mx,curr);
        mn = min(mn,curr);
    }

    long long first = -mn;
    long long last = w - mx;

    if(first > last) cout<<0;
    else cout<<last -first+1;
    return 0;
}
