#include <bits/stdc++.h>

using namespace std;

int main() {
#ifdef _DEBUG
	freopen("input.txt", "r", stdin);
//	freopen("output.txt", "w", stdout);
#endif

	int n, m;
	cin >> n >> m;

	int r = 0;
	for (int i = 0; i < n; ++i) {
		int x;
		cin >> x;

		r += x;
		cout << r / m << ' ';
		r %= m;
	}
	cout << '\n';

	return 0;
}
