#include<bits/stdc++.h>
#define MN 105
using namespace std;
inline int read()
{
    int x=0,f=1;char ch=getchar();
    while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=getchar();}
    while(ch>='0'&&ch<='9'){x=x*10+ch-'0';ch=getchar();}
    return x*f;
}
char c[MN],s;
int n,ans;
int main()
{
	register int i;
	n=read();
	scanf("%s",c+1);
	s=0;
	for (i=1;i<=n;++i)
		if (c[i]!=s)
			if (s) s=0; else s=c[i],++ans;
		else ++ans; 
	printf("%d",ans);
	return 0;	
}
