#include<bits/stdc++.h>
#define MN 105
using namespace std;
inline int read()
{
    int x=0,f=1;char ch=getchar();
    while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=getchar();}
    while(ch>='0'&&ch<='9'){x=x*10+ch-'0';ch=getchar();}
    return x*f;
}
char c[MN];
int n;
int main()
{
	register int i,j;
	n=read();
	scanf("%s",c+1);
	for (i=n/2;i;--i)
	{
		for (j=1;j<=i;++j)
			if (c[j]!=c[i+j]) break;
		if (j>i) break;
	}
	if (i) printf("%d",n-i+1); else printf("%d",n);
}
