string s;

bool read() {
	return !!getline(cin, s);
}

string symmetric = "AHIMOoTUVvWwXxY";
map<char, char> opposite = {{'p', 'q'}, {'q', 'p'}, {'d', 'b'}, {'b', 'd'}};

void solve() {
	forn(i, sz(s)) {
		if (symmetric.find(s[i]) != string::npos) {
			if (s[sz(s) - 1 - i] != s[i]) {
				puts("NIE");
				return;
			}
		} else if (opposite.count(s[i])) {
			if ((sz(s) & 1) && i == (sz(s) >> 1)) {
				puts("NIE");
				return;
			}
			if (s[sz(s) - 1 - i] != opposite[s[i]]) {
				puts("NIE");
				return;
			}
		} else {
			puts("NIE");
			return;
		}
	}
	puts("TAK");
}
