const int N = 2020;

int n;
int x[N], y[N];

bool read() {
	if (!(cin >> n)) return false;
	forn(i, n)
		assert(scanf("%d%d", &x[i], &y[i]) == 2);
	return true;
}

inline li C2(li n) { return n * (n - 1) / 2; }

void solve() {
	map<pti, int> cnt;

	forn(i, n)
		forn(j, i) {
			int cx = x[i] + x[j];
			int cy = y[i] + y[j];
			cnt[{cx, cy}]++;
		}

	li ans = 0;
	for (const auto& p : cnt)
		ans += C2(p.y);
	cout << ans << endl;
}
